INCLUDE LIBMACRO.INC
PROGRAM TABL1
.CONST
DNI DB 'PONEDILOK'
DB 'VIVTOROK '
DB 'SEREDA   '
DB 'CHETVER  '
DB 'PIATNUTSI'
DB 'SYBOTA   '
DB 'NEDILYA  '
PIDK1 DB 'VVED NOMER DN',0
PIDK2 DB 'NOM DN VID 1 DO 7',0
VYVED DB 'DEN - TSE ',0
.DATA
RR DB 9 DUP(' '),0  ;NAZVA DNIA
DCH DB ?  ;LITERA NOMER DNYA
START
MOV AX,03
INT 10H
PV:
WLINEZLN PIDK1
WLINEZLN PIDK2
RCHAR
LINEFEED
MOV DCH,AL
AND AX,000FH
CMP AX,0
JLE PV
CMP AX,7
JG PV
LEA SI,DNI
DEC AX
MOV BX,9
MUL BX
ADD SI,AX
MOV CX,9
LEA DI,RR
REP MOVSB
MOV AL,DCH
WCHAR
LINEFEED
WLINEZ VYVED
WLINEZLN RR

